query FindAllWithClass($payload: StudentMakeupCommentFilterWithClassInput) {
  findAllWithClass(payload: $payload) {
    id
    classId
    sessionId
    classSiteId
    classMakeupId
    sessionMakeupId
    classSiteMakeupId
    status
    studentId
    content
    sendCommentStatus
    commentStatus {
      status
      version
      feedback
    }
    attendance {
      attendanceId
      status
    }
    commentByAreas {
      commentAreaId
      grade
      content
    }
  }
}
